mapper.update:
UPDATE RESERVATION
SET status = 'N'
WHERE user_idx = #{userIdx}
  AND reservation_idx = #{reservationIdx}
  AND room_idx = #{roomIdx}

UPDATE REVIEW
SET review_img_url = #{reviewImgUrl}
WHERE reservation_idx = #{reservationIdx}

mapper.select:
SELECT COUNT(*) count
FROM REVIEW
WHERE USER_IDX = #{user_idx}
